mapper.select:

        SELECT
	    comment_id,
		content,
	 	point_number,
    	reply_number,
    	create_date,
    	article_id,
    	acount_id
	FROM comment_tb


	AND article_id=#{articleId}


	AND acount_id=#{acountId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM comment_tb


	AND article_id=#{articleId}


	AND acount_id=#{acountId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


      SELECT 
        ctb.comment_id,
		ctb.content,
	 	ctb.point_number,
    	ctb.reply_number,
    	ctb.create_date,
    	ctb.article_id,
    	ctb.acount_id,
    	atb.nickname,
    	atb.icon,
    	atb.phone
      FROM comment_tb ctb, acount_tb atb


	AND ctb.article_id=#{articleId}


	AND ctb.acount_id=#{acountId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM comment_tb


	AND article_id=#{articleId}


	AND acount_id=#{acountId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 
	AND ctb.acount_id=atb.acount_id

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(comment_id)
FROM comment_tb


AND article_id=#{articleId}


AND acount_id=#{acountId}
