mapper.select:
SELECT finance_id, money, recharge, consume, withdrawals, self_profit, partner_profit, base_profit, bank_user_name, bank_name, bank_account, bank_address, update_date, acount_id FROM finance_tb
AND money>=#{money}
AND acount_id=#{acountId}
AND ${orderName}
>=
<=
(SELECT ${orderName} FROM finance_tb
AND money>=#{money}
AND acount_id=#{acountId}
ORDER BY ${orderName} ${orderWay} LIMIT #{pageNum}, 1 )
ORDER BY ${orderName} ${orderWay} LIMIT #{pageSize}


SELECT
 	count(finance_id)
FROM finance_tb


AND  money>=#{money}


	AND  acount_id=#{acountId}

select.if:
SELECT COALESCE(SUM(money),0) userExpenditure, 0 platformRevenue, DATE(create_date) create_date FROM flow_water_tb
AND acount_id=#{acountId}
AND DATE(create_date)>= DATE(#{startDate})
AND DATE(create_date)<= DATE(#{endDate})
AND type=#{type}
GROUP BY DATE(create_date)

SELECT 0 userExpenditure, COALESCE(SUM(money),0) platformRevenue, DATE(create_date) create_date FROM flow_water_tb
AND acount_id= #{acountId}
AND DATE(create_date)>= DATE(#{startDate})
AND DATE(create_date)<= DATE(#{endDate})
AND type=#{type}
GROUP BY DATE(create_date)
