mapper.select:

        SELECT
	    novice_task_id,
		frequency,
	 	money,
    	create_date,
    	acount_id
	FROM novice_task_tb


	AND DATE(create_date)=DATE(#{createDate})


	AND acount_id=#{acountId}


	AND frequency=#{frequency}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM novice_task_tb


	AND DATE(create_date)=DATE(#{createDate})


	AND acount_id=#{acountId}


	AND frequency=#{frequency}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(novice_task_id)
FROM novice_task_tb


AND DATE(create_date)=DATE(#{createDate})


AND acount_id=#{acountId}


AND frequency=#{frequency}
